/**
 * Base entity interface
 */
export interface BaseEntity {
  id: string
  createdAt: Date
  updatedAt: Date
}

/**
 * API response wrapper
 */
export interface ApiResponse<T> {
  data: T
  message?: string
  error?: string
}

/**
 * Paginated response
 */
export interface PaginatedResponse<T> {
  data: T[]
  pagination: {
    page: number
    limit: number
    total: number
    totalPages: number
  }
}

/**
 * Form field error
 */
export interface FieldError {
  field: string
  message: string
}

/**
 * Form state
 */
export interface FormState {
  errors?: FieldError[]
  message?: string
  success?: boolean
}

/**
 * User type
 */
export interface User extends BaseEntity {
  name: string
  email: string
  image?: string
  role: 'admin' | 'user'
}

/**
 * API error
 */
export class ApiError extends Error {
  constructor(
    message: string,
    public statusCode: number,
    public details?: any
  ) {
    super(message)
    this.name = 'ApiError'
  }
}
