import React, { createContext, useContext, useState, ReactNode } from 'react';

interface {context_name}State {
  // Define your state shape here
  value: string;
}

interface {context_name}ContextType {
  state: {context_name}State;
  updateValue: (value: string) => void;
  // Add other actions here
}

const {context_name}Context = createContext<{context_name}ContextType | undefined>(undefined);

interface {context_name}ProviderProps {
  children: ReactNode;
}

export function {context_name}Provider({ children }: {context_name}ProviderProps) {
  const [state, setState] = useState<{context_name}State>({
    value: '',
  });

  const updateValue = (value: string) => {
    setState((prev) => ({ ...prev, value }));
  };

  const contextValue: {context_name}ContextType = {
    state,
    updateValue,
  };

  return (
    <{context_name}Context.Provider value={contextValue}>
      {children}
    </{context_name}Context.Provider>
  );
}

export function use{context_name}() {
  const context = useContext({context_name}Context);
  if (context === undefined) {
    throw new Error('use{context_name} must be used within a {context_name}Provider');
  }
  return context;
}
