import React from 'react';
import { render, fireEvent, waitFor } from '@testing-library/react-native';
import {component_name} from '../{component_name}';

describe('{component_name}', () => {
  it('renders correctly', () => {
    const { getByText } = render(<{component_name} />);
    expect(getByText('Expected text')).toBeTruthy();
  });

  it('handles user interaction', () => {
    const mockOnPress = jest.fn();
    const { getByTestId } = render(
      <{component_name} onPress={mockOnPress} testID="button" />
    );

    fireEvent.press(getByTestId('button'));
    expect(mockOnPress).toHaveBeenCalledTimes(1);
  });

  it('updates state correctly', async () => {
    const { getByText, getByTestId } = render(<{component_name} />);

    fireEvent.changeText(getByTestId('input'), 'New value');

    await waitFor(() => {
      expect(getByText('New value')).toBeTruthy();
    });
  });

  it('handles loading state', () => {
    const { getByTestId } = render(<{component_name} loading={true} />);
    expect(getByTestId('loading-indicator')).toBeTruthy();
  });

  it('handles error state', () => {
    const error = { message: 'Something went wrong' };
    const { getByText } = render(<{component_name} error={error} />);
    expect(getByText('Something went wrong')).toBeTruthy();
  });

  it('matches snapshot', () => {
    const tree = render(<{component_name} />).toJSON();
    expect(tree).toMatchSnapshot();
  });
});
